import React from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

class LoginPageComponent extends React.Component {
  constructor(props) {
    super();
    this.state = {
      userName: "",
      userNameValue: "hruday@gmail.com",
      passwordValue: "hruday123",
      password: "",
    };
  }

  handleChange = (name) => (event) => {
    console.log(name);
    console.log(event);
    // this.setState({ name: event.target.value });
  };

  loginAction = () => {
    if (this.state.userName === this.state.userNameValue && this.state.password === this.state.passwordValue) {
      this.props.history.push("/");
    } else {
      alert("user name and password is incorrect");
    }
  };

  render() {
    return (
      <div style={{ display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column" }}>
        <TextField
          type="text"
          id="outlined-helperText"
          value={this.state.userName}
          onChange={this.handleChange("userName")}
          variant="outlined"
        />
        <TextField
          type="text"
          variant="outlined"
          id="outlined-helperText"
          value={this.state.password}
          onChange={this.handleChange("password")}
        />
        <Button onClick={() => this.loginAction()} variant="contained" color="primary">
          Login
        </Button>
      </div>
    );
  }
}

export default LoginPageComponent;
